import React, { Component } from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import _ from 'lodash'
import './ProductDetails.css'

// const data = {
//   id: 1,
//   name: "Trashy Blonde",
//   isFavourite: true,
//   img:"https://upload.wikimedia.org/wikipedia/commons/thumb/a/aa/CocaColaBottle_background_free.png/200px-CocaColaBottle_background_free.png",
//   description: "You know you shouldn't",
//   details: "You know you shouldn't, You know you shouldn't, You know you shouldn't",
//   ibu: 41.5,
//   abv: 4.1,
//   ebc: 15,
//   servedWith: {
//     "fresh crab and lemon",
//     "goats cheese salad"
//   }
// }

const styles = {
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: 200,
    maxHeight: 150,
  },
}

class ProductDetails extends Component {
  render() {
    const {classes} = this.props;
    return (
      <div className="product-details">

        <div className="product-card">
          <Card>
            <CardContent>
              <div className="details">
                <div className="product-img">
                  <img src={this.props.product.img} className={this.props.classes.img}/>
                </div>
                <div className="title">
                  <h3 style={{color: "orange", margin: "10px"}}>{this.props.product.name}</h3>
                  <h4 style={{color: "#b3b3b3", margin: "10px"}}>{this.props.product.description}</h4>
                  <div>
                    <p><em>IBU: </em>{this.props.product.ibu}
                        <em>ABV: </em>{this.props.product.ibu}%
                        <em>EBC: </em>{this.props.product.ibu}</p>
                  </div>
                </div>
                <div className="summary">
                  <p>{this.props.product.details}</p>
                  <h6>Best served with:</h6>
                    <ol>
                      {_.map(this.props.product.servedWith, (serving) => {
                        return <li>{serving}</li>
                      })}
                    </ol>
                </div>
              </div>

              <div className="suggestions">
                <h3>You might also like:</h3>
              </div>
            </CardContent>

          </Card>
        </div>

      </div>
    );
  }
}

ProductDetails.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ProductDetails);
