import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';


import { Link} from "react-router-dom";


const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
    marginLeft: '30%',
    width: '40%',
    minWidth: 300,
    padding: 20,
    borderRadius: 10,
    boxShadow: '0 3px 10px 2px rgba(169,169,169, .3)'
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: '100%',
  },
  button: {
    margin: theme.spacing.unit,
    width: '40%',
    marginLeft: '5%',
    color: "white",
    borderRadius: 10
  }
});

class Signup extends Component {

  constructor(props) {
    super(props)

    this.state = {
      name: '',
      email: '',
      password: '',
      verifyPassword: '',
    };
  }



  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  render() {
    const { classes } = this.props;

    return (
      <div className={classes.container} style={{backgroundColor: "white"}}>
        <Typography variant="h4" gutterBottom style={{marginLeft: "35%"}}>
          Sign up
        </Typography>
        <form noValidate autoComplete="off">
          <TextField
            id="name"
            label="Name"
            placeholder="Name"
            className={classes.textField}
            value={this.state.name}
            onChange={this.handleChange('name')}
            margin="normal"
          />
          <TextField
            id="email"
            label="Email"
            placeholder="Email"
            className={classes.textField}
            value={this.state.email}
            onChange={this.handleChange('email')}
            margin="normal"
          />
          <TextField
            id="password"
            label="Password"
            placeholder="Password"
            className={classes.textField}
            value={this.state.password}
            onChange={this.handleChange('password')}
            margin="normal"
          />
          <TextField
            id="verify-password"
            label="Verify Password"
            placeholder="Verify Password"
            className={classes.textField}
            value={this.state.verifyPassword}
            onChange={this.handleChange('verifyPassword')}
            margin="normal"
          />
          <Button href="/" variant="contained" size="medium" style={{backgroundColor: "grey"}} className={classes.button}>
            Cancel
          </Button>
          <Button type="submit" variant="contained" size="medium" className={classes.button} style={{marginLeft: "5%", backgroundColor: "black"}}>
            Sign up
          </Button>
        </form>
      </div>
    );
  }
}

Signup.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Signup);
