export function getData() {
  const res = fetch('https://api.punkapi.com/v2/beers')
              .then( response => {
                return response.json();
              }).then(arr => {
                // Map beers
                const beers = arr.map(function(drink) {

                  // console.log(drink);

                  // Grab the props we want
                  const beer = {
                    id: drink.id,
                    name : drink.name,
                    tagline : drink.tagline,
                    image : drink.image_url,
                    isFavourite: false
                  }

                  return beer
                })
                return beers
              })

  console.log("res", res);
  return dispatch({type: "GET_DATA", payload: res})
}
