import React, { Component } from 'react';
import { connect }                  from "react-redux"

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import _ from 'lodash'
import Icon from '@material-ui/core/Icon';
import StarBorder from '@material-ui/icons/StarBorder';
import StarOutlined from '@material-ui/icons/StarOutlined';

import ProductDetails from '../ProductDetails/ProductDetails'

import products from '../products'

import {getData, getProductDetails} from '../../store/actions/products'



import './ProductList.css'

/*
  properties:
  data: id, img, name, isFavourite, description



*/


const styles = {
  root: {
    flexGrow: 1,
  },
  card: {
    maxWidth: 350,
    minWidth: 200,
    height: 300,
    margin: 20
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: 200,
    maxHeight: 150,
  },
  icon: {
    fontSize: 32,
    color: "orange"
  }
};




class ProductList extends Component {

  constructor(props) {
    super(props)

    this.state = {details: {}, suggestions: []}
  }

  showStar (isFavourite, id) {
    if (isFavourite) {
      return <StarOutlined className={this.props.classes.icon} onClick={() => {
          console.log("star", id, this.state.products[_.findIndex(this.state.products, {'id' : id})], _.findIndex(this.state.products, {'id' : id}));
          let index = _.findIndex(this.state.products, {'id' : id})

          this.setState({
            products: [
               ...this.state.products.slice(0,index),
               Object.assign({}, this.state.products[index], {isFavourite: false}),
               ...this.state.products.slice(index+1)
            ]
          })
        }}/>
    } else {
      return <StarBorder className={this.props.classes.icon} onClick={() => {
        let index = _.findIndex(this.state.products, {'id' : id})

        this.setState({
          products: [
             ...this.state.products.slice(0,index),
             Object.assign({}, this.state.products[index], {isFavourite: true}),
             ...this.state.products.slice(index+1)
          ]
        })
        }}/>
    }
  }

  // getProductDetails(id) {
  //   // fetch('https://api.punkapi.com/v2/beers/'+id)
  //   // .then(response => response.json())
  //   // .then(arr => {
  //   //   console.log("detail", _.first(arr));
  //   //   this.setState({details: _.first(arr)})
  //   // })
  //
  //   console.log("productDetails", id, _.find(this.props.data, {'id': id}));
  //
  //   this.setState({details: _.find(this.props.data, {'id': id})})
  //   // return _.find(this.props.data, {'id': id})
  // }
  Product (name, tagline, image, isFavourite, id) {
    return <Card className={this.props.classes.card} onClick={(e) => {
        if (e.target.tagName == "DIV") {
          // this.setState({details: this.state.products[_.findIndex(this.state.products, {'id' : id})]})
          this.props.getProductDetails(id)
          this.getSuggestions()
          document.getElementById("productDetails").style.display = "block"
        }
      }}>
      <CardContent>
        <div className="star" style={{float: "right"}}>{this.showStar(isFavourite, id)}</div>
        <div className="product-list-img">
          <img src={image}
              className={this.props.classes.img} style={{marginLeft: "auto", marginRight: "auto", display: "block"}}/>
        </div>
        <h3 style={{color: "orange", margin: "10px"}}>{name}</h3>
        <h4 style={{color: "#b3b3b3", margin: "10px"}}>{tagline}</h4>
      </CardContent>
    </Card>
  }

  list(data, isFav) {
      return _.map(data, (product) => {
        if (isFav && product.isFavourite) {
          return (
            <Grid item xs={12} sm={6} md={4} lg={4}>
              {this.Product(product.name, product.tagline, product.image, product.isFavourite, product.id)}
            </Grid>
          )
        } else if (!isFav) {
          return (
            <Grid item xs={12} sm={6} md={4} lg={4}>
              {this.Product(product.name, product.tagline, product.image, product.isFavourite, product.id)}
            </Grid>
          )
        }

      })
  }

  getSuggestions(id) {
    // fetch('https://api.punkapi.com/v2/beers')
    // .then(response => response.json())
    // .then(arr => {
    //   this.setState({suggestions: arr})
    // })
    return this.setState({suggestions: _.sampleSize(this.props.data, 3)})
  }

  getBeers() {
    console.log("search", document.getElementById("search").value);
    fetch('https://api.punkapi.com/v2/beers' )
      .then( response => {
        return response.json();
      }).then(arr => {
        // Map beers
        const beers = arr.map(function(drink) {

          // console.log(drink);

          // Grab the props we want
          const beer = {
            id: drink.id,
            name : drink.name,
            tagline : drink.tagline,
            image : drink.image_url,
            isFavourite: false
          }

          return beer
        })

        this.setState({products: beers})

        console.log("state", this.state.products);
      })
    }

  componentDidMount() {
     this.getBeers();
     this.props.getData();
     // this.props.getProductDetails();
  }



  render() {
    const { classes } = this.props;
    return (
      <div className="product-list">
        <div className="products">
          <Grid container spacing={32} alignItems="center">
            {this.list(this.props.data, this.props.isFav)}
          </Grid>
        </div>
        <div id="productDetails" style={{display: "none"}}>
          <ProductDetails product={this.props.productDetails || {}} suggestions={this.state.suggestions} />
        </div>
      </div>
    )
  }
}

ProductList.propTypes = {
  classes: PropTypes.object.isRequired,
  data: PropTypes.object.isRequired,
  productDetails: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => {
  console.log("state", state)
  return {
    data : state.data.data,
    productDetails : state.data.details
  }
}

const mapDispatchToProps = (dispatch) => ({
  getData       : () => dispatch(getData()),
  getProductDetails : (id) => dispatch(getProductDetails(id))
})

export default withStyles(styles)(connect(mapStateToProps, mapDispatchToProps)(ProductList));
