    description: "You know you shouldn't"
  }
]

const styles = {
  root: {
    flexGrow: 1,
  },
  card: {
    maxWidth: 350,
    minWidth: 200,
    height: 300,
    margin: 20
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  }
};




class ProductList extends Component {

  Product (name, description, img) {
    return <Card className={this.props.classes.card}>
      <CardContent>
        <img src={img}/>
        <h3>{name}</h3>
        <p>{description}</p>
      </CardContent>
    </Card>
  }

  list(data) {
    return _.map(data, (product) => {
      return (
        <Grid item xs={12} sm={6} md={4} lg={4}>
          {this.Product(product.name, product.description)}
        </Grid>
    )
    })
  }



  render() {
    const { classes } = this.props;
    return (
      <div className="product-list">
        <div className="products">
          <Grid container spacing={30} alignItems="center">
            {this.list(data)}
          </Grid>
        </div>
      </div>
    )
  }
}

ProductList.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ProductList);
