import {LOGIN_REQUEST, LOGIN_SUCCESS, LOGIN_FAILURE,
        LOGOUT_REQUEST, LOGOUT_SUCCESS, LOGOUT_FAILURE} from './constants';

import axios from 'axios';

function requestLogin() {
  return {
    type            : LOGIN_REQUEST,
    isFetching      : true,
    isAuthenticated : false
  }
}

function recieveLogin() {
  return {
    type            : LOGIN_SUCCESS,
    isFetching      : false,
    isAuthenticated : true,
    // jwt             : user.token,
    // auth            : user.auth
  }
}

function loginError(message) {
  return {
    type            : LOGIN_FAILURE,
    isFetching      : false,
    isAuthenticated : false,
    message
  }
}

function requestLogout() {
  return {
    type: LOGOUT_REQUEST,
    isFetching: true,
    isAuthenticated: true
  }
}

function receiveLogout() {
  return {
    type: LOGOUT_SUCCESS,
    isFetching: false,
    isAuthenticated: false
  }
}


export const login = () => {
  return dispatch => {
    dispatch(requestLogin())
    console.log("Calling login");
    dispatch(recieveLogin())
  }
}

export function logoutUser() {
  return dispatch => {
    dispatch(requestLogout())
    dispatch(receiveLogout())
  }
}
