const getDataReducer = (state = [], {type, payload}) => {
    switch (type) {
      case "GET_DATA":
        return {'data': payload} || state
        case "GET_DETAILS":
          return {'details': payload} || state
      default:
        return state
    }
}

export default getDataReducer;
