import React, { Component } from 'react';
import { connect }          from "react-redux"

import PropTypes            from 'prop-types';
import _                    from 'lodash'

import { withStyles }       from '@material-ui/core/styles';
import  { Card, CardContent,
          Grid, Icon }      from '@material-ui/core';
import { StarBorder,
          StarOutlined }    from '@material-ui/icons';

import ProductDetails       from '../ProductDetails/ProductDetails'

import {getData,
        getProductDetails,
        updateFavourites }  from '../../store/actions/products'

import './ProductList.css'


const styles = {
  root: {
    flexGrow: 1,
  },
  card: {
    maxWidth: 350,
    minWidth: 200,
    height: 300,
    margin: 20
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: 200,
    maxHeight: 150,
  },
  icon: {
    fontSize: 32,
    color: "orange"
  }
};

class ProductList extends Component {

  constructor(props) {
    super(props)

    this.state = {products: {}, suggestions: [], favourites: []}
  }

  showStar (isFavourite, id) {
    if (isFavourite || _.indexOf(this.props.favourites, id) != -1) {
      return <StarOutlined className={this.props.classes.icon} onClick={() => {
          console.log("clicked star");
          this.props.updateFavourites(_.pull(this.props.favourites, id))
          this.setState({favourites : _.pull(this.props.favourites, id)})
          // console.log("star", id, this.state, this.state.products[_.findIndex(this.state.products, {'id' : id})], _.findIndex(this.state.products, {'id' : id}));
          // let index = _.findIndex(this.state.products, {'id' : id})
          //
          // this.setState({
          //   products: [
          //      ...this.state.products.slice(0,index),
          //      Object.assign({}, this.state.products[index], {isFavourite: false}),
          //      ...this.state.products.slice(index+1)
          //   ]
          // })
        }}/>
    } else if (isFavourite && _.indexOf(this.props.favourites, id) == -1) {
      this.props.updateFavourites(_.concat(this.props.favourites, id))
    } else {
      return <StarBorder className={this.props.classes.icon} onClick={() => {
          this.props.updateFavourites(_.concat(this.props.favourites, id))
          this.setState({favourites : _.concat(this.props.favourites, id)})
        // let index = _.findIndex(this.state.products, {'id' : id})
        //
        // this.setState({
        //   products: [
        //      ...this.state.products.slice(0,index),
        //      Object.assign({}, this.state.products[index], {isFavourite: true}),
        //      ...this.state.products.slice(index+1)
        //   ]
        // })
        }}/>
    }
  }

  Product (name, tagline, image, isFavourite, id) {
    return <Card className={this.props.classes.card} onClick={(e) => {
        console.log("e", e.target);
        if (e.target.tagName == "DIV") {
          // this.setState({details: this.state.products[_.findIndex(this.state.products, {'id' : id})]})
          this.props.getProductDetails(id)
          this.getSuggestions()
          document.getElementById("productDetails").style.display = "block"
        }
      }}>
      <CardContent>
        <div className="star" style={{float: "right"}}>{this.showStar(isFavourite, id)}</div>
        <div className="product-list-img">
          <img src={image}
              className={this.props.classes.img} style={{marginLeft: "auto", marginRight: "auto", display: "block"}}/>
        </div>
        <h3 style={{color: "orange", margin: "10px"}}>{name}</h3>
        <h4 style={{color: "#b3b3b3", margin: "10px"}}>{tagline}</h4>
      </CardContent>
    </Card>
  }

  list(data, isFav) {
      return _.map(data, (product) => {
        if (isFav && product.isFavourite) {
          return (
            <Grid item xs={12} sm={6} md={4} lg={4}>
              {this.Product(product.name, product.tagline, product.image, product.isFavourite, product.id)}
            </Grid>
          )
        } else if (!isFav) {
          return (
            <Grid item xs={12} sm={6} md={4} lg={4}>
              {this.Product(product.name, product.tagline, product.image, product.isFavourite, product.id)}
            </Grid>
          )
        }

      })
  }

  getSuggestions(id) {
    return this.setState({suggestions: _.sampleSize(this.props.data, 3)})
  }

  componentDidMount() {
     this.props.getData();
     this.props.updateFavourites();
  }



  render() {
    const { classes } = this.props;
    return (
      <div className="product-list">
        <div className="products">
          <Grid container spacing={32} alignItems="center">
            {this.list(this.props.data, this.props.isFav)}
          </Grid>
        </div>
        <div id="productDetails" style={{display: "none"}}>
          <ProductDetails product={this.props.productDetails || {}} suggestions={this.state.suggestions} />
        </div>
      </div>
    )
  }
}

ProductList.propTypes = {
  classes: PropTypes.object.isRequired,
  data: PropTypes.object.isRequired,
  productDetails: PropTypes.object.isRequired,
  favourites : PropTypes.array.isRequired
};

const mapStateToProps = (state) => {
  return {
    data : state.data || {},
    productDetails : state.details || {},
    favourites : state.favourites || []
  }
}

const mapDispatchToProps = (dispatch) => ({
  getData       : () => dispatch(getData()),
  getProductDetails : (id) => dispatch(getProductDetails(id)),
  updateFavourites : (favourites) => dispatch(updateFavourites(favourites))
})

export default withStyles(styles)(connect(mapStateToProps, mapDispatchToProps)(ProductList));
