import React, { Component } from 'react';
import { connect }          from "react-redux"
import PropTypes            from 'prop-types';
import { withStyles }       from '@material-ui/core/styles';
import { Link }             from "react-router-dom";

import { Button, TextField,
               Typography } from '@material-ui/core';

import { login }            from "../../store/actions/login"


const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
    marginLeft: '30%',
    marginTop: '10%',
    width: '40%',
    minWidth: 300,
    padding: 20,
    borderRadius: 10,
    boxShadow: '0 3px 10px 5px rgba(169,169,169, .4)'
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: '100%',
  },
  button: {
    margin: theme.spacing.unit,
    width: '35%',
    marginLeft: '65%',
    backgroundColor: "black",
    color: "white",
    borderRadius: 10
  },
  signupBtn: {
    margin: theme.spacing.unit,
    marginLeft: "80%"
  },
  links: {
    margin: theme.spacing.unit,
    width: '100%',
    borderRadius: 20
  },
  img: {
    width: 15,
  },
  fbimg: {
    width: 25,
  }
});

class Login extends Component {

  constructor(props) {
    super(props)

    this.state = {
      email: '',
      password: '',
    };

    this.submit = this.submit.bind(this)
  }

  handleChange = name => event => {
    this.setState({ [name]: event.target.value });
  };

  submit(e, value) {
    e.preventDefault()
    this.props.login()
    this.props.history.push("/home")
  }

  render() {
    const { classes } = this.props;

    return (
      <div className={classes.container} style={{backgroundColor: "white"}}>
        <Button href="/signup" className={classes.signupBtn}>
          Sign up
        </Button>
        <Typography variant="h4" gutterBottom style={{marginLeft: "35%"}}>
          Sign in
        </Typography>
        <form noValidate autoComplete="off" onSubmit={this.submit}>
          <TextField
            id="email"
            label="Email"
            placeholder="Email"
            className={classes.textField}
            value={this.state.email}
            onChange={this.handleChange('email')}
            margin="normal"
          />
          <TextField
            id="password"
            label="Password"
            placeholder="Password"
            className={classes.textField}
            value={this.state.password}
            onChange={this.handleChange('password')}
            margin="normal"
            />
          <Button type="submit" variant="contained" size="medium" className={classes.button}>
              Sign in
            </Button>
            <br/>
            <Button variant="outlined" size="medium"  href="/" className={classes.links} >
              <img className={classes.img} src="https://cdn.freebiesupply.com/logos/large/2x/google-icon-logo-png-transparent.png"/>
              &nbsp;&nbsp;
              Sign in using Google
            </Button>

            <br/>
            <Button variant="outlined" size="medium" href="/" className={classes.links}>
              <img className={classes.fbimg} src="http://cofarming.info/wp-content/uploads/2017/01/facebook-icon-preview-1-400x400.png" />
              &nbsp;&nbsp;
              Sign in using Facebook
            </Button>
        </form>
      </div>
    );
  }
}

Login.propTypes = {
  isAuthenticated : PropTypes.bool.isRequired,
  classes         : PropTypes.object.isRequired,
  history         : PropTypes.object.isRequired,
  login           : PropTypes.func.isRequired,
};

const mapStateToProps = (state) => {
  console.log("login", state);
  return {
    isAuthenticated : state.auth.isAuthenticated
  }
}

const mapDispatchToProps = (dispatch) => ({
  login       : () => dispatch(login())
})

export default withStyles(styles)(connect(mapStateToProps, mapDispatchToProps)(Login));
