import React, { Component } from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';

import _ from 'lodash'
import './ProductDetails.css'

// const data = {
//   id: 1,
//   name: "Trashy Blonde",
//   isFavourite: true,
//   img:"https://upload.wikimedia.org/wikipedia/commons/thumb/a/aa/CocaColaBottle_background_free.png/200px-CocaColaBottle_background_free.png",
//   description: "You know you shouldn't",
//   details: "You know you shouldn't, You know you shouldn't, You know you shouldn't",
//   ibu: 41.5,
//   abv: 4.1,
//   ebc: 15,
//   servedWith: {
//     "fresh crab and lemon",
//     "goats cheese salad"
//   }
// }

const styles = {
  img: {
    margin: 'auto',
    display: 'block',
    width: "60%"
    // maxWidth: 200,
    // maxHeight: 150,
  },
}

class ProductDetails extends Component {
  suggestion(suggestions) {
    console.log("suggestions", suggestions);
    return _.map(suggestions, (suggestion) => {
      return (
        <Grid item xs={12} sm={6} md={4} lg={3}>
          <div className="suggestion">
            <img src={suggestion.image} style={{width: "30px"}}/>
            <h3 style={{paddingTop: "50px", float: left}}>{suggestion.name}</h3>
          </div>
        </Grid>
      )
    })
  }
  render() {
    const {classes} = this.props;
    return (
      <div className="product-details">
        <div className="product-card">
          <Card style={{wdith: "100%"}}>
            <CardContent>
              <span class="close" onClick={() => document.getElementById("productDetails").style.display = "none"}>&times;</span>
              <Grid container spacing={24}>
                <Grid item xs={12} sm={3}>
                  <div className="product-img">
                    <img src={this.props.product.image_url} className={this.props.classes.img}/>
                  </div>
                </Grid>
                <Grid item xs={12} sm={9}>
                  <div className="details">
                    <div className="title">
                      <h3 style={{color: "orange", fontSize: "25px"}}>{this.props.product.name}</h3>
                      <h4 style={{color: "#b3b3b3"}}>{this.props.product.tagline}</h4>
                      <div>
                        <p><b>IBU: </b>{this.props.product.ibu} &nbsp;&nbsp;&nbsp;
                           <b>ABV: </b>{this.props.product.abv}% &nbsp;&nbsp;&nbsp;
                           <b>EBC: </b>{this.props.product.ebc}</p>
                      </div>
                    </div>
                    <div className="summary">
                      <p>{this.props.product.description}</p>
                      <h3>Best served with:</h3>
                        <ul>
                          {_.map(this.props.product.food_pairing, (serving) => {
                            return <li>{serving}</li>
                          })}
                        </ul>
                  </div>
                </div>
              </Grid>
            </Grid>
            <br/>
            <div className="suggestions">
              <h3 style={{color: "orange", fontSize: "25px"}}>You might also like:</h3>
            </div>
            <Grid container spacing={24}>
                {this.suggestion(this.props.suggestions)}
            </Grid>
          </CardContent>

        </Card>
      </div>

    </div>
    );
  }
}

ProductDetails.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ProductDetails);
