export const getData = () => async dispatch => {
  const res = await fetch('https://api.punkapi.com/v2/beers')
              .then( response => {
                return response.json();
              }).then(arr => {
                // Map beers
                const beers = arr.map(function(drink) {

                  // Grab the props we want
                  const beer = {
                    id: drink.id,
                    name : drink.name,
                    tagline : drink.tagline,
                    image : drink.image_url,
                    isFavourite: false
                  }

                  return beer
                })
                return beers
              })

  console.log("res", res);

  dispatch({type: "GET_DATA", payload: res})
}

export const getSearchedData = (value) => async dispatch => {
  const res = await fetch('https://api.punkapi.com/v2/beers?beer_name='+value)
              .then( response => {
                return response.json();
              }).then(arr => {
                // Map beers
                console.log("array", arr);
                const beers = arr.map(function(drink) {

                  // Grab the props we want
                  const beer = {
                    id: drink.id,
                    name : drink.name,
                    tagline : drink.tagline,
                    image : drink.image_url,
                    isFavourite: false
                  }

                  return beer
                })
                return beers
              }).catch(err => {
                console.log("err", err);
              })

  console.log("search res", res);

  dispatch({type: "GET_SEARCHED_DATA", payload: res})
}


export const getProductDetails = (id) => async dispatch => {

  const res = await fetch('https://api.punkapi.com/v2/beers/' + id)
              .then(response => response.json())
              .then(arr => {
                console.log("detail", arr[0]);
                return arr[0]
              })

  console.log("res", res);

  dispatch({type: "GET_DETAILS", payload: res })
}



