import React, { Component } from 'react';

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import _ from 'lodash'
import Icon from '@material-ui/core/Icon';
import StarBorder from '@material-ui/icons/StarBorder';
import StarOutlined from '@material-ui/icons/StarOutlined';



import './ProductList.css'

/*
  properties:
  data: id, img, name, isFavourite, description



*/

const data = [
  {
    id: 1,
    name: "Trashy Blonde",
    isFavourite: true,
    img:"https://upload.wikimedia.org/wikipedia/commons/thumb/a/aa/CocaColaBottle_background_free.png/200px-CocaColaBottle_background_free.png",
    description: "You know you shouldn't"
  },
  {
    id: 1,
    name: "Trashy Blonde",
    isFavourite: true,
    img:"https://upload.wikimedia.org/wikipedia/commons/thumb/a/aa/CocaColaBottle_background_free.png/200px-CocaColaBottle_background_free.png",
    description: "You know you shouldn't"
  },
  {
    id: 1,
    name: "Trashy Blonde",
    isFavourite: false,
    img:"https://upload.wikimedia.org/wikipedia/commons/thumb/a/aa/CocaColaBottle_background_free.png/200px-CocaColaBottle_background_free.png",
    description: "You know you shouldn't"
  },
  {
    id: 1,
    name: "Trashy Blonde",
    isFavourite: true,
    img:"https://upload.wikimedia.org/wikipedia/commons/thumb/a/aa/CocaColaBottle_background_free.png/200px-CocaColaBottle_background_free.png",
    description: "You know you shouldn't"
  },
  {
    id: 1,
    name: "Trashy Blonde",
    isFavourite: false,
    img:"https://upload.wikimedia.org/wikipedia/commons/thumb/a/aa/CocaColaBottle_background_free.png/200px-CocaColaBottle_background_free.png",
    description: "You know you shouldn't"
  }
]

const styles = {
  root: {
    flexGrow: 1,
  },
  card: {
    maxWidth: 350,
    minWidth: 200,
    height: 300,
    margin: 20
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: 200,
    maxHeight: 150,
  },
  icon: {
    fontSize: 32,
    color: "orange"
  }
};




class ProductList extends Component {

  constructor(props) {
    super(props)

    this.state = {products: data}
  }

  showStar (isFavourite, id) {
    if (isFavourite) {
      return <StarOutlined className={this.props.classes.icon} onClick={() => {
          console.log("star", _.findIndex(this.state.products, {'id' : id}));

          // this.setState({products[_.findIndex(this.state.products, {'id' : id})].isFavourite : false})
        }}/>
    } else {
      return <StarBorder className={this.props.classes.icon} onClick={() => {
          // this.setState({products[_.findIndex(this.state.products, {'id' : id})].isFavourite : true})
        }}/>
    }
  }
  Product (name, description, img, isFavourite, id) {
    return <Card className={this.props.classes.card}>
      <CardContent>
        {this.showStar(isFavourite, id)}
        <img src={img}
        className={this.props.classes.img}/>
        <h3 style={{color: "orange", margin: "10px"}}>{name}</h3>
        <h4 style={{color: "#b3b3b3", margin: "10px"}}>{description}</h4>
      </CardContent>
    </Card>
  }

  list(data) {
    return _.map(this.state.products, (product) => {
      return (
        <Grid item xs={12} sm={6} md={4} lg={4}>
          {this.Product(product.name, product.description, product.img, product.isFavourite, product.id)}
        </Grid>
    )
    })
  }



  render() {
    const { classes } = this.props;
    return (
      <div className="product-list">
        <div className="products">
          <Grid container spacing={30} alignItems="center">
            {this.list(data)}
          </Grid>
        </div>
      </div>
    )
  }
}

ProductList.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ProductList);
